import React from 'react';
import './PointsSection.css';
import PointsTally from './PointsTally';
import { usePoints } from '../context/PointsContext';

const PointsSection = ({ user, rewards }) => {
  const { isRewardRedeemed } = usePoints();

  const currentPoints = user.points;

  //Filter only unredeemed rewards
  const unredeemedRewards = rewards.filter(reward => !isRedeemReward(reward.id)).sort((a, b) => a.reward_points - b.reward_points);

  //Find the next reward the user qualifies for
  const nextReward = unredeemedRewards.find(
    reward => currentPoints < reward.reward_points
  );

  const pointsNeeded = nextReward ? nextReward.reward_points : currentPoints;
  const pointsRemaining = Math.max(pointsNeeded - currentPoints, 0);
  const progressPercentage = nextReward ? (currentPoints / nextReward.reward_points) * 100 : 100;

  return (
    <div className="section">
      <h2 className="heading">Points</h2>
      <div className="points-card">
        <PointsTally points={currentPoints} />
        <div className="points-progress-box">
          <div className="progress-left">
            <p className="progress-message">
              Purchase more from Braddex in order to avail your next awesome reward!
            </p>
          </div>
          <div className="progress-divider"></div>
          <div className="progress-right">
            <p className="points-remaining">
              {pointsRemaining} more points until you can redeem your next reward!
            </p>
            <div className="progress-bar-container">
              <div
                className="progress-bar-fill"
                style={{ width: `${progressPercentage}%` }}
              ></div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PointsSection;
