import React, { useState } from 'react';
import './RewardsSection.css';

const RewardsSection = () => {
  const [selectedVoucher, setSelectedVoucher] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  const vouchers = [
    {
      id: 1,
      name: "Free Drink of Any Choice",
      description: "Enjoy a complimentary beverage of your choice from our extensive menu. Valid for any size and any drink type.",
      topColor: "#a22221",
      bottomColor: "#ffca7a"
    },
    {
      id: 2,
      name: "50% Off on Pastries",
      description: "Get half off on any pastry item. Perfect for pairing with your favorite coffee or tea.",
      topColor: "#8B4513",
      bottomColor: "#DDA0DD"
    },
    {
      id: 3,
      name: "Buy 1 Get 1 Free",
      description: "Purchase any item and get another one of equal or lesser value absolutely free!",
      topColor: "#228B22",
      bottomColor: "#FFB6C1"
    }
  ];

  const handleSeeMore = (voucher) => {
    setSelectedVoucher(voucher);
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
    setSelectedVoucher(null);
  };

  const handleClaimVoucher = () => {
    // Add your voucher claiming logic here
    alert(`Voucher "${selectedVoucher.name}" claimed successfully!`);
    handleCloseModal();
  };

  return (
    <div className="rewards-section">
      <h2 className="heading">Rewards</h2>
      <div className="rewards-container">
        {vouchers.map((voucher) => (
          <div key={voucher.id} className="reward-card">
            <div 
              className="reward-top" 
              style={{ backgroundColor: voucher.topColor }}
            >
              <h3 className="voucher-name">{voucher.name}</h3>
            </div>
            <div 
              className="reward-bottom" 
              style={{ backgroundColor: voucher.bottomColor }}
            >
              <button 
                className="see-more-btn"
                onClick={() => handleSeeMore(voucher)}
              >
                See More
              </button>
            </div>
          </div>
        ))}
      </div>

      {/* Modal */}
      {isModalOpen && selectedVoucher && (
        <div className="modal-overlay" onClick={handleCloseModal}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h2>{selectedVoucher.name}</h2>
              <button className="close-btn" onClick={handleCloseModal}>Ã—</button>
            </div>
            <div className="modal-body">
              <p>{selectedVoucher.description}</p>
            </div>
            <div className="modal-footer">
              <button className="claim-btn" onClick={handleClaimVoucher}>
                Claim Voucher?
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default RewardsSection;
